def missing_values(df):
    total=df.isnull().sum().sort_values(ascending=False)
    percent = round(df.isnull().sum().sort_values(ascending = False)/len(df)*100,4)
    return pd.concat([total, percent], axis=1, keys=['Total','Percent'])

